sys_variable_value.value:
(function execute(inputs, outputs) {
  var templateTools = new x_apig_recurring_t.TemplateTools();
  //Basic template record fields
  templateTools.setName(inputs.name);
  templateTools.setTable(inputs.table);
  templateTools.setActive('true');
  templateTools.setUser(inputs.user);
  templateTools.setShortDescription(inputs.shortDescription);
  var doShowOnTemplateBar = inputs.showOnTemplateBar == true ? 'true' : 'false';
  templateTools.setShowOnTemplateBar(doShowOnTemplateBar);

  //Loop through fields and add to the template
  var fields = inputs.fields;
  fields.forEach(function(field){
  templateTools.setValue(field['Field_Name'], field['Field_Value']);
  })


  templateSysId = templateTools.insert();

  outputs.templatesysid = templateSysId;
})(inputs, outputs);

