sys_variable_value.value:
(function execute(inputs, outputs) {
  var start = new GlideDateTime(inputs.runStart);
  var duration = inputs.runPeriod;
  var startTime = new GlideTime(new GlideDateTime(inputs.runTime).getTime());
  gs.info('Run Time: ' + startTime.getValue());

  var scheduledJobTools = new x_apig_recurring_t.ScheduledJobTools();
  scheduledJobTools.initialize();
  scheduledJobTools.setName(inputs.name);
  scheduledJobTools.setActive('true');
  scheduledJobTools.setRunStart(start);
  scheduledJobTools.setRunPeriod(duration);
  scheduledJobTools.setRunTime(startTime);
  scheduledJobTools.setTemplate(inputs.template);

  var scheduleSysId = scheduledJobTools.insert();
  outputs.scheduled_task_sysid = scheduleSysId;
})(inputs, outputs);

