sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

	if(newValue.toString() != ''){
		var relatedInstructionsLink = document.getElementById('x_apig_recurring_t_x_rc_task.related_instructions_link');

		try {
			var url = g_form.getValue('related_instructions').toString();
			var domain = url.match(/^(?:http?s?:\/\/)?(?:http?:\/\/)?(?:[^@\/\n]+@)?(?:www\.)?([^:\/\n]+)/im)[1];
			if (domain.includes("servicenow.com") || domain.includes("apigroup")) {
				var ga = new GlideAjax('x_apig_recurring_t.RCTaskUtils');
				ga.addParam('sysparm_name', 'getTaskShortDescription');
				ga.addParam('sysparm_url', url);
				ga.getXMLAnswer(function(answer) {
					relatedInstructionsLink.innerHTML = answer || domain;
				});
			} else {
				relatedInstructionsLink.innerHTML = domain;
			}
		} catch (error) {
			relatedInstructionsLink.innerHTML = g_form.getLabelOf('related_instructions');
		}
	}
}
