sys_script.script:
function getMonthName() {
    var gdt = new GlideDateTime();

    var monthIndex = gdt.getMonthUTC();

    // Array of month names
    var monthNames = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];

    return monthNames[monthIndex - 1];
}

function getQuarterName(currentMonthName) {
    var quarters = {
        'January': 'Q1',
        'February': 'Q1',
        'March': 'Q1',
        'April': 'Q2',
        'May': 'Q2',
        'June': 'Q2',
        'July': 'Q3',
        'August': 'Q3',
        'September': 'Q3',
        'October': 'Q4',
        'November': 'Q4',
        'December': 'Q4'
    };
    return quarters[currentMonthName];
}

function getYear() {
    var gdt = new GlideDateTime();
    return gdt.getYearUTC();
}

function getServicePrefix(serviceSysId) {
    var serviceGr = new GlideRecord('cmdb_ci_service');
    if (serviceGr.get(serviceSysId)) {
        return serviceGr.u_sec0_chars.toString() + '.';
    }
    return '';
}

(function executeRule(current, previous /*null when async*/ ) {
    if (current.u_sox_control.toString() != '') {



        var currentMonthName = getMonthName();
        var currentMonthOrQuarter = currentMonthName;
        var currentQuarterName = '';
        var recurrence = "";

        if (current.description.contains('Frequency: Quarterly')) {
            currentQuarterName = getQuarterName(currentMonthName);
            currentMonthOrQuarter = currentQuarterName;
            recurrence = "Quarterly";
        } else if (current.description.contains('Frequency: Monthly')) {
            recurrence = "Monthly";
        } else if (current.description.contains('Frequency: Event Driven')) {
            recurrence = "Event Driven";
        } else if (current.description.contains('Frequency: Annually')) {
            currentQuarterName = getQuarterName(currentMonthName);
            currentMonthOrQuarter = currentQuarterName;
            recurrence = "Annually";
        } else if (current.description.contains('Frequency: Semi-Annually')) {
            currentQuarterName = getQuarterName(currentMonthName);
            currentMonthOrQuarter = currentQuarterName;
            recurrence = "Semi-Annually";
        }

        current.setValue('short_description', "SOX Control - " + getServicePrefix(current.business_service.sys_id.toString()) + current.u_sox_control.toString() + " - " + recurrence + " - " + current.short_description.toString() + ' - ' + currentMonthOrQuarter + ' ' + getYear());
    }
})(current, previous);
