sys_ui_action.client_script_v2:
function onClick(g_form) {
    var ga = new GlideAjax('sn_tcm_collab_hook.MSTeamsChatAjaxProcessor');
    ga.addParam('sysparm_name', 'isLoggedInUserHasValidEmail');
    ga.getXMLAnswer(function(answer) {
        if (answer + '' == 'true') {
            g_modal.sn_tcm_collab_hook.startMsTeamsChat({
                title: getMessage("Start Microsoft Teams Chat"),
                height: '450px',
                size: 'lg',
                confirmTitle: getMessage("Start Chat"),
                cancelTitle: getMessage("Cancel"),
                params: {
                    table: g_form.getTableName(),
                    sysId: g_form.getUniqueValue()
                }
            }).then(function(result) {
                g_ui_scripts.getUIScript("sn_tcm_collab_hook.chatSwarmModalUtils").then(function(getChatSwarmModalUtilsWS) {
                    getChatSwarmModalUtilsWS().MSTeamsOpenMultiUserChat({
                        items: result.data.items,
                        topicName: result.data.chatTitle,
                        journalField: "work_notes",
                        tableName: g_form.getTableName(),
                        sysId: g_form.getUniqueValue(),
                        initiatedBy: g_user.userID
                    });
                });
            });
        } else
            getMessage("You do not have a valid email to start Microsoft Teams chat", function(msg) {
                g_form.addErrorMessage(msg);
            });
    });
}
